import React from 'react';
import { post } from 'axios';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import {withStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Toolbar from "@material-ui/core/Toolbar";

const styles = theme =>({
    hidden : {
        display: 'none'
    }
});


class UserLogin extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            id : '',
            account : '',
            testRoomNum : '',
            applicantNum : '',
            open : false
        }
    }

    addCustomer = () => {
        const url = '/api/customers';
        const formData = new FormData();
        formData.append('image', this.state.file);
        formData.append('name', this.state.userName);
        formData.append('birthday', this.state.birthday);
        formData.append('gender', this.state.gender);
        formData.append('job', this.state.job);
        const config = {
            header : {
                'content-type' : 'multipart/form-data'
            }
        }
        return post(url, formData, config);

    }

    loginOpen = () => {
        this.setState({
            loginOpen: true
        });
    }
    loginClose = () => {
        this.setState({
            loginOpen : false
        })
    }

    handleFormSubmit = (e) => {
        e.preventDefault()
        this.addCustomer()
            .then((response) => {
                console.log(response.data);
                this.props.stateRefresh();
            })
        this.setState({
            account : '',
            password : '',
            open : false
        })
    }
    handleValueChange = (e) => {
        let nextState = {};
        nextState[e.target.name] = e.target.value;
        this.setState(nextState);
    }

    handleFormSubmit = (e) => {
        e.preventDefault()
        this.addCustomer()
            .then((response) => {
                console.log(response.data);
                this.props.stateRefresh();
            })
        this.setState({
            file : null,
            userName : '',
            birthday : '',
            gender : '',
            job : '',
            fileName : '',
            open : false
        })
    }

    render() {
        const { classes } = this.props;

        return (
            <div>
                <Button color="inherit" onClick={this.loginOpen}>Login</Button>
                <Dialog open={this.state.loginOpen} onClose={this.loginClose}>
                    <DialogTitle onClose={this.loginClose} className={classes.menu}>로그인</DialogTitle>
                    <DialogContent>
                        <Typography gutterBottom>
                            학교명
                            <TextField className={classes.menu} label="학교명" variant="outlined" name="account" value={this.state.account} onChange={this.handleValueChange} /><br/>
                            비밀번호
                            <TextField className={classes.menu} label="비밀번호" variant="outlined" name="password" value={this.state.password} onChange={this.handleValueChange} />
                        </Typography>
                    </DialogContent>
                    <DialogActions className={classes.menu}>
                        <Button variant="contained" color="primary" onClick={this.handleFormSubmit}>로그인</Button>
                        <Button variant="outlined" color="primary" onClick={this.loginClose}>닫기</Button>
                    </DialogActions>
                </Dialog>

            </div>
        )
    }
}
export default withStyles(styles)(CustomerAdd);
