import React, {Component} from 'react';
import Customer from "./components/Customer";
import CustomerAdd from "./components/CustomerAdd";
import Pager from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import {withStyles} from "@material-ui/core/styles";
import CircularProgress from '@material-ui/core/CircularProgress';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import { fade, makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import Button from '@material-ui/core/Button';
import CustomerDelete from "./components/CustomerDelete";
import Users from "./users/User";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import DialogActions from "@material-ui/core/DialogActions";
import Dialog from "@material-ui/core/Dialog";

const styles = theme => ({
  root: {
    width : '100%',
    minWidth : 1080
  },
  menu:{
    marginTop : 15,
    marginBottom : 15,
    display:'flex',
    justifyContent: 'center'
  },
  paper:{
    marginLeft: 18,
    marginRight: 18
  },
  progress:{
    margin: theme.spacing(1) * 2
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    display: 'none',
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  tableHead:{
    fontSize: '1.0rem'
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(1),
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  }
})

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      customers: '',
      users:'',
      completed: 0,
      searchKeyword: '',
      loginState: false
    }
  }

  stateRefresh = () => {
    this.setState({
      customers: '',
      users:'',
      completed: 0,
      searchKeyword: '',
      loginState: false
    });
    this.callApi()
        .then(res => this.setState({customers: res}))
        .catch(err => console.log(err));
  }

  componentDidMount() {
    this.timer = setInterval(this.progress, 20);
    this.callApi()
        .then(res => this.setState({customers: res}))
        .catch(err => console.log(err));

    this.callUser()
        .then(res => this.setState({users : res}))
        .catch(err => console.log(err));
  }
  callUser = async () => {
    const response = await fetch('/api/users/1');
    const bodyUser = await response.json();

    return bodyUser;
  }
  callApi = async () => {
    const response = await fetch('/api/customers');
    const body = await response.json();

    return body;
  }
  progress = () => {
    const {completed} = this.state;
    this.setState({completed: completed >= 100 ? 0 : completed + 1});
  }
  handleValueChange = (e) => {
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    this.setState(nextState);
  }

  render() {
    const {classes} = this.props;
    const userCellList = ["시험장 번호", "시험장 명", "시험실 수", "총 지원자 수"];
    return (
        <div className={classes.root}>
          <AppBar position="static">
            <Toolbar>
              <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                <MenuIcon />
              </IconButton>
              <Typography variant="h6" className={classes.title}>
                2022 수능 시스템
              </Typography>
              <Button color="inherit">Login</Button>
              <Dialog open={this.state.open} onClose={this.handleClose}>
                <DialogTitle onClose={this.handleClose}>삭제 경고</DialogTitle>

                <DialogContent>
                  <Typography gutterBottom>
                    선택한 고객 정보가 삭제됩니다.
                  </Typography>
                </DialogContent>
                <DialogActions>
                  <Button variant="contained" color="primary" onClick={(e)=>{this.deleteCustomer(this.props.id)}}>삭제</Button>
                  <Button variant="outlined" color="primary" onClick={this.handleClose}>닫기</Button>
                </DialogActions>
              </Dialog>
            </Toolbar>
          </AppBar>
          <Pager className={classes.paper}>
            <Table className={classes.table}>
              <TableHead>
                <TableRow>
                  {
                    userCellList.map(c => {
                      return <TableCell className={classes.tableHead}>{c}</TableCell>
                    })
                  }
                </TableRow>
              </TableHead>
              <TableBody>
                {
                  this.state.users ? this.state.users.map(u => {
                        return (<Users stateRefresh={this.stateRefresh} key={u.id} id={u.id} account={u.account} testRoomNum={u.testRoomNum} applicantNum={u.applicantNum}/>);
                      }) :
                      <TableRow>
                        <TableCell colSpan="6" align="center">
                          <CircularProgress className={classes.progress} variant="determinate"
                                            value={this.state.completed}/>
                        </TableCell>
                      </TableRow>
                }
              </TableBody>
            </Table>
          </Pager>
        </div>
    );
  }
}


export default withStyles(styles)(App);
